drop table usuarios;create table usuarios(    cddocumento varchar(30)not null primary key,    cdtipo_documento varchar(4) not null,    dsprimer_nombre varchar(150) not null,    dssegundo_nombre varchar (150),    dsprimer_apellido varchar (150) not null,    dssegundo_apellido varchar(150),    dsemail varchar(80)not null,    nmcelualr integer,    fenacimiento date,    dsfacebook varchar(60),    dsinstagram varchar(60),    dswhatsapp varchar(60),    dsyoutube varchar(60),    dsotra_redsocial varchar(60),    dsusername varchar(80) not null,    dspassword varchar(20) not null,    feultimo_ingreso timestamp with time zone,       cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,    CONSTRAINT usuarios_dsusername_key UNIQUE (dsusername));drop table profesiones;create table profesiones(    id SERIAL primary key,    dsnombre varchar(30),    dsimagen varchar(100),         cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,	    CONSTRAINT profesiones_dsnombre_key UNIQUE (dsnombre));drop table profesiones_usuarios;create table profesiones_usuarios(    id_profesion integer,    cddocumento varchar(30),       cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,	    CONSTRAINT profesiones_usuarios_pkey primary key (id_profesion,cddocumento),    CONSTRAINT profesiones_usuarios_f1 FOREIGN KEY (id_profesion) references profesiones(id),    CONSTRAINT profesiones_usuarios_f2 FOREIGN KEY (cddocumento) references usuarios(cddocumento));drop table diplomas_usuarios;create table diplomas_usuarios(    id SERIAL primary key,    id_profesion integer,    cddocumento varchar(30),    dsruta_diploma varchar(150),    cdtarjeta_profesion varchar(80),    fediploma date,        cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,    CONSTRAINT diplomas_usuarios_f1 FOREIGN KEY (id_profesion) references profesiones(id),    CONSTRAINT diplomas_usuarios_f2 FOREIGN KEY (cddocumento) references usuarios(cddocumento)   );drop table paises;create table paises(    id SERIAL primary key,    cdpais varchar(15),    cdindicativo varchar(15),    cdmoneda varchar(10),       cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,	    CONSTRAINT paises_cdpais_key UNIQUE (cdpais),    CONSTRAINT paises_cdindicativo_key UNIQUE (cdindicativo));drop table departamentos;create table departamentos(    id SERIAL primary key,    cddepartamento varchar(15),    dsdepartamento varchar(30),    id_pais integer,    cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,    CONSTRAINT departamentos_f1 FOREIGN KEY (id_pais) references paises(id),    CONSTRAINT departamentos_cddepartamento_key UNIQUE (cddepartamento));drop table ciudades;create table ciudades(    id SERIAL primary key,    cdciudad varchar(15),    cdindicativo varchar(15),        dsciudad varchar(30),    iddepartamento integer,    cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,	    CONSTRAINT ciudades_f1 FOREIGN KEY (iddepartamento) references departamentos(id),    CONSTRAINT ciudades_cdciudad_key UNIQUE (cdciudad),    CONSTRAINT ciudades_cdindicativo_key UNIQUE (cdindicativo));drop table ciudades_usuarios;create table ciudades_usuarios(    idciudad integer,    cddocumento varchar(30),    snpreferencia varchar(1) default 'N',    cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,	    CONSTRAINT ciudades_usuarios_f1 FOREIGN KEY (idciudad) references ciudades(id),    CONSTRAINT ciudades_usuarios_f2 FOREIGN KEY (cddocumento) references usuarios(cddocumento));drop table medios_pago;create table medios_pago(    id SERIAL primary key,    cdmedio_pago varchar(30),    dsmedio_pago varchar(50),    cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP);drop table medios_pago_usuarios;create table medios_pago_usuarios(    idmedio_pago integer,        cddocumento varchar(30),    snactivo varchar(1) default 'S',    cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,    CONSTRAINT medios_pago_usuarios_pkey PRIMARY KEY (idmedio_pago,cddocumento),    CONSTRAINT medios_pago_usuarios_f1 FOREIGN KEY (idmedio_pago) references medios_pago(id),    CONSTRAINT medios_pago_usuarios_f2 FOREIGN KEY (cddocumento) references usuarios(cddocumento));drop table servicios_disponibles;create table servicios_disponibles(    id SERIAL primary key,        cddocumento varchar(30),    id_profesion integer,    idciudad integer,    dsdescripcion varchar(250),    fedisponible_inicio date,    fedisponible_fin date,    nmvalor_inicio numeric,    nmvalor_fin numeric,    sndisponible varchar(1) default 'N',    cdestado varchar(1) not null,    cdusuario varchar(25),	dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,	dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,	    CONSTRAINT servicios_f1 FOREIGN KEY (idciudad) references ciudades(id),    CONSTRAINT servicios_f2 FOREIGN KEY (cddocumento) references usuarios(cddocumento),    CONSTRAINT servicios_f3 FOREIGN KEY (id_profesion) references profesiones(id),    CONSTRAINT servicios_idciudad_cddocumento_id_profesion_key UNIQUE (idciudad,cddocumento,id_profesion));drop table servicios_clientes;create table servicios_clientes(  idservicio integer,  cddocumento_cliente varchar(30),  dsdireccion varchar(100),  dscomplemento_direccion varchar(50),  nmtelefono_contacto varchar(20),  dsdescipcion text,  dsimagen1 varchar(100),  dsimagen2 varchar(100),  dsimagen3 varchar(100),   dsimagen4 varchar(100),  idmedio_pago integer,  feservicio date,  nmcalificacion integer,  nmvalor        numeric,  dscomentarios  varchar(250),  dsimagen1_termina varchar(100),  dsimagen2_termina varchar(100),  dsimagen3_termina varchar(100),   dsimagen4_termina varchar(100),  cdestado varchar(1) not null,  cdusuario varchar(25),  dtfechacreacion timestamp with time zone default CURRENT_TIMESTAMP,  dtfechamodificacion timestamp with time zone default CURRENT_TIMESTAMP,  CONSTRAINT servicios_clientes_pkey PRIMARY KEY (idservicio,cddocumento_cliente,feservicio,cdestado),  CONSTRAINT servicios_f1 FOREIGN KEY (idservicio) references servicios_disponibles(id),  CONSTRAINT servicios_f2 FOREIGN KEY (cddocumento_cliente) references usuarios(cddocumento));